import React, { useEffect, useState } from "react";
import { Row } from "react-bootstrap";
import { Pagination } from "./Pagination";
import { Product } from "./Product";

export const Home = () => {
  const [products, setproduct] = useState([]);
  const [allProducts, setallProducts] = useState([]);
  const [page, setpage] = useState(1);
  const fetchProduct = () => {
    fetch("https://dummyjson.com/products")
      .then((res) => res.json())
      .then((res) => setproduct(res.products));
  };

  const options = {
    method: "GET",
    headers: {
      "X-RapidAPI-Key": "ff348215damsha3d759bf549f745p1cbe43jsnc25d0e88001c",
      "X-RapidAPI-Host": "youtube-mp36.p.rapidapi.com",
    },
  };

  const fetchYoutube = () => {
    fetch("https://youtube-mp36.p.rapidapi.com/dl?id=UxxajLWwzqY", options)
      .then((response) => response.json())
      .then((response) => console.log("youtube" + response))
      .catch((err) => console.error(err));
  };
  useEffect(() => {
    localStorage.setItem("cartItems", [{ test: 1 }]);
    fetchProduct();
  }, []);

  console.log(products.slice(10 * (page - 1), 10 * (page - 1) + 10));

  return (
    <>
      <Row className="d-flex justify-content-around">
        {products
          .slice(10 * (page - 1), 10 * (page - 1) + 10)
          .map((product) => (
            <Product key={product.id} product={product} />
          ))}
      </Row>
      <Pagination page={page} setpage={setpage} len={products.length / 10} />
    </>
  );
};
