import React, { useEffect, useState } from "react";
import { Row } from "react-bootstrap";
import { Pagination } from "./Pagination";
import { Product } from "./Product";

export const Home = () => {
  const [products, setproduct] = useState([]);
  const [allProducts, setallProducts] = useState([]);
  const [page, setpage] = useState(1);
  const fetchProduct = () => {
    fetch("https://dummyjson.com/products")
      .then((res) => res.json())
      .then((res) => setallProducts(res.products))
      .then((res) => setproduct(allProducts.slice(1, 10)));
  };

  useEffect(() => {
    localStorage.setItem("cartItems", [{ test: 1 }]);
    fetchProduct();
  }, []);

  useEffect(() => {
    setproduct(allProducts.slice(10 * (page - 1), 10 * (page - 1) + 10));
  }, [page]);
  console.log(products.slice(10 * (page - 1), 10 * (page - 1) + 10));

  return (
    <>
      <Row className="d-flex justify-content-around">
        {products.map((product) => (
          <Product key={product.id} product={product} />
        ))}
      </Row>
      <Pagination page={page} setpage={setpage} len={products.length / 10} />
    </>
  );
};
