import React from "react";
import { useProvider } from "./BuyProvider";
import { Product } from "./Product";

export const Cart = () => {
  const { cartCount, cartItems } = useProvider();
  // console.log(provider);
  return (
    <div>
      Cart{cartCount}\
      {cartItems.map((cart) => (
        <Product product={cart} />
      ))}
    </div>
  );
};
