import React, {
  Children,
  createContext,
  useContext,
  useEffect,
  useState,
} from "react";
import { json } from "react-router";

const BuyProviders = createContext();

export const BuyProvider = ({ children }) => {
  const fetchProduct = () => {
    console.log("inside fetch");
    fetch("https://dummyjson.com/products")
      .then((res) => res.json())
      .then((res) => {
        setallProducts(res.products);
        setproduct(res.products.slice(0, 10));
      });
  };

  useEffect(() => {}, []);
  const [products, setproduct] = useState([]);

  const [cartItems, setcartItems] = useState(
    JSON.parse(localStorage.getItem("cartItems")) || []
  );
  const [cartCount, setcartCount] = useState(cartItems.length);

  const handleAddToCart = (newCart) => {
    console.log("inside cart", newCart);
    const isNewCart = cartItems.find((cart) => cart.id == newCart.id);

    if (!isNewCart) {
      const newItems = [...cartItems, newCart];
      setcartItems(newItems);

      localStorage.setItem("cartItems", JSON.stringify(newItems));
      setcartCount((cart) => setcartCount(cart + 1));
    }
  };
  return (
    <BuyProviders.Provider
      value={{
        cartCount: cartCount,
        setcartCount,
        cartItems,
        setcartItems,
        handleAddToCart,
        products,
        setproduct,
      }}
    >
      {children}
    </BuyProviders.Provider>
  );
};

export const useProvider = () => {
  return useContext(BuyProviders);
};
